actionConfiguration.body:
() => {
  if (Switch1.isSwitchedOn) {
    setInterval(() => {
      const cpuUtilData = appsmith.store.cpu_util_data;
      const diskUtilData = appsmith.store.disk_util_data;
      PiData.run();
      storeValue("cpu_util_data", [...cpuUtilData, {
        x: PiData.data.cpu_percent,
        y: PiData.data.cpu_percent
      }]);
      storeValue("disk_util_data", [...diskUtilData, {
        x: PiData.data.disk_usage_percent,
        y: PiData.data.disk_usage_percent
      }]);
    }, 1500, 'timerId');
  } else {
    clearInterval('timerId');
  }
}

