async (fn, params) => {
  try {
    const {email} = jsonwebtoken.verify(appsmith.store.token, 'secret');
    return setuser.run({
      email
    }).then(() => fn.run({
      ...params,
      email
    }));
  } catch (error) {
    return showAlert('Session expired. Please re-login', 'warning').then(() => appsmith.mode !== 'EDIT' ? navigateTo('login') : '');
  }
}

