	run: async(fn, params) => {
		try{
			const {email} = jsonwebtoken.verify(appsmith.store.token, 'secret');
			return setuser.run({email})
				.then(() => fn.run({...params, email}));
		} catch(error){
			return showAlert('Session expired. Please re-login', 'warning')
				.then(() => appsmith.mode !== 'EDIT'? navigateTo('login'): '');
		}
	},
	init: async () => {
		return await this.run(gettodos);
	},
	createTodo: () => {
		return this.run(createtodo, Table1.newRow)
			.then(() => this.run(gettodos))
	},
	updateTodo: () => {
		return this.run(updatetodo, Table1.updatedRow)
			.then(() => this.run(gettodos))
	},
	deleteTodo: () => {
		return this.run(deletetodo, Table1.triggeredRow)
			.then(() => this.run(gettodos))
	},
	logOut: () => {
		removeValue('token')
		.then(() => navigateTo('login'));
	}
}

