	chgData: (data, arr, mul) => data.map((d, i) => {
		if(!~arr.findIndex(a => a==i)) return d;	
		return {...d, value: d._ + (d._ * mul)}
	}),
	gendata: () => {
		const max = 25000;
		const min = 10000;

		const data = this.days()
		.map(d => ({label: d, value: Math.floor(Math.random() * (max - min + 1) + min)}))
		.map(d => ({...d, _: d.value}));

		storeValue('data', data);
		storeValue('backup', data);
	},
	zoomData: () => {
		const backup = appsmith.store.backup;
		storeValue('data', backup.slice(RangeSlider1.start-1, RangeSlider1.end));
	},
	middleGain: async () => {
		const slider = NumberSlider2.value;
		let data = appsmith.store.data;
		const length = data.length;

		if(length%2) {
			const mid = Math.floor((length/2));
			data = this.chgData(data, [mid], slider);
		} else {
			const mid2 = (length/2)
			const mid1 = mid2-1;
			data = this.chgData(data, [mid1, mid2], slider);
		}
		storeValue('data', data);
	},
	startGain: async () => {
		const slider = NumberSlider1.value;
		let data = appsmith.store.data;
		const length = data.length;

		if(length < 4) {
			data = this.chgData(data, [0], slider);
		} else {
			data = this.chgData(data, [0, 1], slider);
		}
		storeValue('data', data);
		return data;
	},
	endGain: async () => {
		const slider = NumberSlider3.value;
		let data = appsmith.store.data;
		const length = data.length;
		const end = length-1;

		if(length < 4) {
			data = this.chgData(data, [end], slider);
		} else {
			data = this.chgData(data, [end, end-1], slider);
		}
		storeValue('data', data);
	}
}
