export default {
	calcAnalytics: () => {
		const data = {open: 0, unassigned: 0, openCritical: 0, openDue: 0};
		getTickets.data.data.tickets.forEach(t => {
			if(t.status == 'open') {
				data.open += 1;
				if(t.priority == 'high') data.openCritical += 1;
				if(!moment(t?.createdAt).isSame(new Date(), 'week')) data.openDue += 1;
			}
			if(!t?.assignedTo) data.unassigned += 1;
		});
		return data;
	},
	calcTicketsByCategory () {
		const agg = getTickets.data.data.tickets.filter(t => t.status==='open').reduce((a,c)=>{ 
			a[c.category] += 1;
			return a;
		}, {account: 0, software: 0, operations: 0, other: 0});
		return Object.keys(agg).map(i => ({x: i, y: agg[i]}))
	},
	calcTicketsByPriority () {
		const agg = getTickets.data.data.tickets.filter(t => t.status==='open').reduce((a,c)=>{ 
			a[c.priority] += 1;
			return a;
		}, {low: 0, medium: 0, high: 0});
		return Object.keys(agg).map(i => ({x: i, y: agg[i]}))
	},
}

