export default {
	statuses: ['todo', 'in-progress', 'done'],
	getTodos: async () => {
		const todos = await get_todos.run();
		storeValue('todos', todos);
	},
	updateTodo: async (id, update) => {
		//make local change
		const todos = appsmith.store.todos;
		const temp = Object.assign([], appsmith.store.todos);

		const i = todos.findIndex(t => t._id === id);
		todos[i] = {...todos[i], ...update};
		await storeValue('todos', todos);

		//make network request
		try {
			//throw new Error("Couldn't write to the DB");
			await update_todo.run({id, update});
			await this.getTodos();
		} catch(e) {
			showAlert(e.message, 'error');
			storeValue('todos', temp)
		}
	},
	moveLeft: async (todo=List3.selectedItem) => {
		const i = this.statuses.indexOf(todo.status);
		if(i > 0) return await this.updateTodo(todo._id, {status: this.statuses[i-1]})
	},
	moveRight: async (todo) => {
		const i = this.statuses.indexOf(todo.status);
		if(i < this.statuses.length -1) return await this.updateTodo(todo._id, {status: this.statuses[i+1]})
	},
	editTodo: async () => {
		await this.updateTodo(appsmith.store.activeTodo._id, JSONForm1.formData);
		closeModal('Modal1')
	}
}

